        //Расчёт ИМТ принимая на вход рост и вес выводит строку рост+вес+имт
        public string RashetIMT(string healthRnd)
        {
            double IMT = 0;
            if (healthRnd == "ожирение" || healthRnd == "анорексия" || healthRnd == "отличное")
            {
                if (healthRnd == "ожирение")
                {
                    IMT = 41;
                    height = 150;
                    weight = 100;
                }
                if (healthRnd == "анорексия")
                {
                    IMT = 10;
                    height = 200;
                    weight = 40;
                }
                if (healthRnd == "отличное")
                {
                    IMT = rnd.Next(19, 24);
                    height = 200;
                    weight = 90;
                }

                physique = height.ToString() + "см" + " " + weight.ToString() + "кг";

            }
            else
            {
                weight = rnd.Next(40, 150); //генератор роста
                height = rnd.Next(140, 210);//генератор веса
                physique = height.ToString() + "см" + " " + weight.ToString() + "кг";
                IMT = weight / (height / 100 * height / 100);
            }


            if (IMT < 16)
            {
                physique += " ИМТ: Выраженный дефецит массы тела"; //Телосложение

            }
            if (IMT >= 16 && IMT < 18.5)
            {
                physique += " ИМТ: Недостаточная масса тела"; //Телосложение
            }
            if (IMT >= 18.5 && IMT < 25)
            {
                physique += " ИМТ: Нормальная масса тела"; //Телосложение
            }
            if (IMT >= 25 && IMT < 30)
            {
                physique += " ИМТ: Избыточная масса теле(предожирение)"; //Телосложение

            }
            if (IMT >= 30 && IMT < 35)
            {
                physique += " ИМТ: Ожирение 1-ой степени"; //Телосложение
            }
            if (IMT >= 35 && IMT < 40)
            {
                physique += " ИМТ: Ожирение 2-ой степени"; //Телосложение

            }
            if (IMT > 40)
            {
                physique += " ИМТ: Ожирение третий степени"; //Телосложение
            }
            return physique;
        }

