            {
                stadia = 8;
                stroka += HealthStad(stadia);
            }
            else if (healthRnd == "болезнь Альцгеймера")
            {
                stadia = 7;
                stroka += HealthStad(stadia);
            }
            else stroka += " " + rnd.Next(0, 101) + "%";
            return stroka;
        }

        //стадии болезней
        public string HealthStad(int stadia)
        {
            string stroka = null;
            string[] StadiaM = new string[] { " первая стадия", " вторая стадия", " третья стадия", " четвертая стадия", " пятая стадия", " шестая стадия", " седьмая стадия", " депресивная фаза", " маниакальная фаза" };
            switch (stadia)
            {
                case 1:
                    stroka = StadiaM[0]; //первая
                    break;
                case 2:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//первая-вторая
                    break;
                case 3:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//первая-третья
                    break;
                case 4:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//третья-четвёртая
                    break;
                case 5:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//четвёртая-пятая
                    break;
                case 6:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//пятая-шестая
                    break;
                case 7:
                    stroka = StadiaM[rnd.Next(0, stadia - 1)];//шестая седьмая
                    break;
                case 8:
                    stroka = StadiaM[rnd.Next(7, stadia - 1)] + StadiaM[rnd.Next(0, 6)]; //биполярка+ стадия от 1-5
                    break;
                default:
                    break;
            }

            return stroka;
        }

