        //формирование бункера
        public string Bunker(string[] oborudovanie, string[] estInBunker)
        {

            string Bunker;
            sBunker = rnd.Next(80, 350);
            Bunker = "Площадь убежища: " + sBunker.ToString() + " квадратных метров" + "\n";
            Bunker += "Время нахождения в убежище (Еда и питье рассчитаны на весь период пребывания): " + timeInBunker[rnd.Next(0, timeInBunker.Length - 1)] + "\n";
            Bunker += sostoynieBunker[rnd.Next(0, sostoynieBunker.Length - 1)] + "\n";
            Bunker += "В бункер оборудовано: " + "\n" + oborudovanie[0] + "\n" + oborudovanie[1] + "\n" + oborudovanie[2] + "\n";
            Bunker += "В бункер есть: " + "\n" + estInBunker[0] + "\n" + estInBunker[1] + "\n" + estInBunker[2] + "\n";
            Bunker += "В бункер живут: " + "\n" + JivnostBunker[rnd.Next(0, JivnostBunker.Length - 1)];
            return Bunker;
        }

